import React, { useState, useEffect, useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../Styles/domain.css";
import { fetchDomains, fetchCourseByDomain } from "../utils/api";
import { UserContext } from "../UserContext";

const DomainList = () => {
  const [domains, setDomains] = useState([]);
  const [selectedDomain, setSelectedDomain] = useState();
  const [selectedDomainName, setSelectedDomainName] = useState("");
  const [courses, setCourses] = useState([]);
  const { user, setUser } = useContext(UserContext);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetchDomains();
        setDomains(response.data);
      } catch (error) {
        console.error("Error fetching domains:", error);
      }
    };
    fetchData();
  }, []);

  const handleDomainSelection = async (program, batch) => {
    // Find the domain that matches the selected program and batch
    const domain = domains.find(
      (d) => d.program === program && d.batch === batch
    );

    if (!domain) return;

    setSelectedDomain(domain.id);
    setSelectedDomainName(program);

    try {
      const response = await fetchCourseByDomain(domain.id);
      const data = response.data;
      setCourses(data);
    } catch (error) {
      console.error("Error fetching courses:", error);
    }
  };

  const handleLogout = () => {
    // Clear user data from context and localStorage
    setUser(null);
    localStorage.removeItem("user");
    navigate("/");
  };

  // Get unique branches from domains
  const imtechBranches = domains
    .filter((d) => d.program.includes("iMTech"))
    .map((d) => d.program.replace("iMTech ", ""));

  const mtechBranches = domains
    .filter((d) => d.program.includes("MTech") && !d.program.includes("iMTech"))
    .map((d) => d.program.replace("MTech ", ""));

  return (
    <div>
      {/* Header with user info and logout */}
      <div className="app-header">
        <h1>ACADEMIA</h1>
        <div className="user-info">
          <span className="user-name">Welcome, {user?.name || "User"}</span>
          <button className="logout-btn" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </div>

      <div className="selection-container">
        {/* IMTech Section */}
        <div className="program-category">
          <h3>Integrated MTech</h3>
          <div className="branch-buttons">
            {[...new Set(imtechBranches)].map((branch) => (
              <button
                key={`imtech-${branch}`}
                className="branch-btn"
                onClick={() => handleDomainSelection(`iMTech ${branch}`, "2025")}
              >
                {branch}
              </button>
            ))}
          </div>
        </div>

        {/* MTech Section */}
        <div className="program-category">
          <h3>MTech</h3>
          <div className="branch-buttons">
            {[...new Set(mtechBranches)].map((branch) => (
              <button
                key={`mtech-${branch}`}
                className="branch-btn"
                onClick={() => handleDomainSelection(`MTech ${branch}`, "2025")}
              >
                {branch}
              </button>
            ))}
          </div>
        </div>
      </div>

      {selectedDomain && (
        <div className="timetable-section">
          <div className="timetable-header">
            <h2>{selectedDomainName} - COURSE TIME TABLE</h2>
            <button
              className="back-btn"
              onClick={() => {
                setSelectedDomain(null);
                setCourses([]);
              }}
            >
              &larr; Back to Domains
            </button>
          </div>
          <table>
            <thead>
              <tr>
                <th>Course ID</th>
                <th>Course Name</th>
                <th>Faculty Name</th>
                <th>Faculty Email</th>
                <th>Day</th>
                <th>Time</th>
                <th>Room</th>
                <th>Students</th>
              </tr>
            </thead>
            <tbody>
              {courses.map((course) => (
                <tr key={course.id}>
                  <td>{course.id}</td>
                  <td>{course.name}</td>
                  <td>{course.faculty?.f_name || "TBA"}</td>
                  <td>{course.faculty?.f_email || "TBA"}</td>
                  <td>{course.schedule?.day || "TBA"}</td>
                  <td>{course.schedule?.time || "TBA"}</td>
                  <td>{course.schedule?.room || "TBA"}</td>
                  <td>
                    <button className="view-btn">
                      <Link to={`/courses/${course.id}/students`} className="view-link">
                        View Students
                      </Link>
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default DomainList;
