import "../Styles/StudentList.css";
import React, { useState, useEffect, useContext } from "react";
import { useParams, useNavigate, useLocation } from "react-router-dom";
import { UserContext } from "../UserContext";
import { fetchStudents } from "../utils/api";

const StudentList = () => {
  const { courseId } = useParams();
  const [students, setStudents] = useState([]);
  const navigate = useNavigate();
  const location = useLocation();
  const { user, setUser } = useContext(UserContext);

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem("user");
    navigate("/");
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetchStudents(courseId);
        setStudents(response.data);
      } catch (error) {
        console.error("Error fetching students:", error);
      }
    };
    fetchData();
  }, [courseId]);

  const goBack = () => {
    if (location.state && location.state.previousDomainId) {
      navigate("/domain", {
        state: {
          domainId: location.state.previousDomainId,
          domainName: location.state.previousDomainName,
        },
      });
    } else {
      navigate("/domain");
    }
  };

  return (
    <div>
      <div className="navbar">
        <span>Student List for Course {courseId}</span>
        <button className="logout-btn-navbar" onClick={handleLogout}>
          Logout
        </button>
      </div>

      {/* Back button to close student list and go back */}
      <button className="back-btn" onClick={goBack}>
        &larr; Back
      </button>

      <h1>Students Enrolled</h1>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Roll Number</th>
            <th>Email</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student) => (
            <tr key={student.id}>
              <td>{student.id}</td>
              <td>{student.name}</td>
              <td>{student.rno}</td>
              <td>{student.email}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StudentList;
