import React, { useState, useEffect, useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../Styles/domain.css";
import { fetchDomains, fetchCourseByDomain } from "../utils/api";
import { UserContext } from "../UserContext";

const DomainList = () => {
  const [domains, setDomains] = useState([]);
  const [selectedDomain, setSelectedDomain] = useState();
  const [selectedDomainName, setSelectedDomainName] = useState("");
  const [courses, setCourses] = useState([]);
  const { user, setUser } = useContext(UserContext);
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetchDomains();
        setDomains(response.data);
      } catch (error) {
        console.error("Error fetching domains:", error);
      }
    };
    fetchData();
  }, []);

  const handleDomainSelection = async (program, batch) => {
    setError(null);
    const domain = domains.find(
      (d) => d.program === program && d.batch === batch
    );

    if (!domain) {
      setError("Domain not found");
      return;
    }

    setSelectedDomain(domain.id);
    setSelectedDomainName(program);
    setLoading(true);

    try {
      const response = await fetchCourseByDomain(domain.id);
      setCourses(response.data);
    } catch (err) {
      console.error("Error fetching courses:", err);
      setError("Failed to load courses. Please try logging in again.");
      if (err.response && err.response.status === 401) {
        // Token might be expired
        handleLogout();
      }
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    // Clear user data from context and localStorage
    setUser(null);
    localStorage.removeItem("user");
    navigate("/");
  };

  // Get unique branches from domains
  const imtechBranches = domains
    .filter((d) => d.program.includes("iMTech"))
    .map((d) => d.program.replace("iMTech ", ""));

  const mtechBranches = domains
    .filter((d) => d.program.includes("MTech") && !d.program.includes("iMTech"))
    .map((d) => d.program.replace("MTech ", ""));

  return (
    <div>
      {/* Header with user info and logout */}
      <div className="app-header">
        <h1>ACADEMIA</h1>
        <div className="user-info">
          <span className="user-name">Welcome, {user?.name || "User"}</span>
          <button className="logout-btn" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </div>

      <div className="selection-container">
        {/* IMTech Section */}
        <div className="program-category">
          <h3>Integrated MTech</h3>
          <div className="branch-buttons">
            {[...new Set(imtechBranches)].map((branch) => (
              <button
                key={`imtech-${branch}`}
                className="branch-btn"
                onClick={() => handleDomainSelection(`iMTech ${branch}`, "2025")}
              >
                {branch}
              </button>
            ))}
          </div>
        </div>

        {/* MTech Section */}
        <div className="program-category">
          <h3>MTech</h3>
          <div className="branch-buttons">
            {[...new Set(mtechBranches)].map((branch) => (
              <button
                key={`mtech-${branch}`}
                className="branch-btn"
                onClick={() => handleDomainSelection(`MTech ${branch}`, "2025")}
              >
                {branch}
              </button>
            ))}
          </div>
        </div>
      </div>

      {error && <div className="error-message" style={{ textAlign: 'center', color: 'red', padding: '20px' }}>{error}</div>}

      {loading && <div className="loading-message" style={{ textAlign: 'center', padding: '20px' }}>Loading timetable...</div>}

      {!loading && !error && selectedDomain && (
        <div className="timetable-section">
          <h2>{selectedDomainName} - COURSE TIME TABLE</h2>

          <div className="timetable-grid-container">
            {/* Time Header Row */}
            <div className="grid-header-row">
              <div className="grid-header-cell empty-corner"></div>
              {["09:00 AM", "10:00 AM", "11:00 AM", "12:00 PM", "01:00 PM", "02:00 PM", "03:00 PM", "04:00 PM", "05:00 PM"].map((time) => (
                <div key={time} className="grid-header-cell time-header">
                  {time}
                </div>
              ))}
            </div>

            {/* Days Rows */}
            {["Monday", "Tuesday", "Wednesday", "Thursday", "Friday"].map((day) => (
              <div key={day} className="grid-day-row">
                {/* Day Label */}
                <div className="grid-day-label">{day}</div>

                {/* Time Slots for this Day */}
                {["09:00 AM", "10:00 AM", "11:00 AM", "12:00 PM", "01:00 PM", "02:00 PM", "03:00 PM", "04:00 PM", "05:00 PM"].map((time) => {
                  // Find course for this day and time
                  const course = courses.find(
                    (c) =>
                      c.schedule &&
                      c.schedule.day === day &&
                      c.schedule.time === time
                  );

                  return (
                    <div key={`${day}-${time}`} className="grid-cell">
                      {course ? (
                        <div className="course-card">
                          <div className="course-code">{course.courseCode}</div>
                          <div className="course-name" title={course.name}>{course.name}</div>
                          <div className="course-faculty">
                            <i className="fas fa-chalkboard-teacher"></i> {course.faculty?.f_name || "TBA"}
                          </div>
                          <div className="course-room">
                            <i className="fas fa-map-marker-alt"></i> {course.schedule?.room}
                          </div>
                          <Link to={`/courses/${course.id}/students`} className="view-students-btn">
                            View Students
                          </Link>
                        </div>
                      ) : (
                        <div className="empty-slot"></div>
                      )}
                    </div>
                  );
                })}
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default DomainList;
